$(document).ready(function() {
    // Плавная прокрутка к верху страницы при клике на ссылку "Преимущества"
    $('#goToTop').click(function(event) {
        event.preventDefault(); // Отменяем стандартное поведение ссылки
        $('html, body').animate({
            scrollTop: 0 // Прокручиваем страницу к верху
        }, 800); // Плавно прокручиваем страницу к верху за 800 миллисекунд
    });
});
